SELECT t.parentId,t.id,e.offlineAgentCount,act.accountNum,IFNULL(sum(rpt.validbet),0) validbet, IFNULL(sum(rpt.payout),0) payout,IFNULL(sum(bt.bonusAmount),0) bonusAmount, IFNULL(sum(cc.offlineMemberCount),0) offlineMemberCount,sum(he.handlingCharge)+sum(withdrawHandlingCharge) feeAmount FROM agy_account t LEFT JOIN (SELECT count(*) offlineAgentCount,parentId FROM agy_tree WHERE depth>0 GROUP BY parentId) e on e.parentId = t.id LEFT JOIN (SELECT count(*) accountNum,cagencyId FROM mbr_account GROUP BY cagencyId) act on act.cagencyId = t.id LEFT JOIN agy_tree att ON att.parentId = t.id AND att.depth > 0
LEFT JOIN ( SELECT sum(r.validbet) validbet,sum(r.payout) payout,t.cagencyId FROM ( SELECT userName,sum(validbet) validbet,sum(payout) payout FROM rpt_bet_rcd_day
AND startday >= #{startTime}
AND startday >= #{endTime}
GROUP BY userName) r INNER JOIN mbr_account t ON r.username = t.loginName GROUP BY t.cagencyId) rpt ON att.childNodeId = rpt.cagencyId
LEFT JOIN ( SELECT sum(s.bonusAmount) bonusAmount,r.cagencyId FROM opr_act_bonus s INNER JOIN mbr_account r ON s.accountId = r.id
s.`status` = 1
AND DATE_FORMAT(s.applicationTime,'%Y-%m-%d') >= #{startTime}
AND DATE_FORMAT(s.applicationTime,'%Y-%m-%d') <= #{endTime}
GROUP BY r.cagencyId ) bt ON att.childNodeId = bt.cagencyId
LEFT JOIN (SELECT count(*) offlineMemberCount,cagencyId from mbr_account GROUP BY cagencyId) cc ON cc.cagencyId = att.childNodeId
LEFT JOIN ( SELECT IFNULL(sum(t.handlingCharge),0) handlingCharge,c.cagencyId FROM fund_deposit t LEFT JOIN mbr_account c ON t.accountId = c.id
t.`status` = 1 AND t.feeType = 1
AND DATE_FORMAT(t.createTime,'%Y-%m-%d') >= #{startTime}
AND DATE_FORMAT(t.createTime,'%Y-%m-%d') <= #{endTime}
GROUP BY c.cagencyId ) he ON he.cagencyId = att.childNodeId
LEFT JOIN ( SELECT IFNULL(sum(w.handlingCharge),0) withdrawHandlingCharge,mt.cagencyId FROM fund_acc_withdraw w LEFT JOIN mbr_account mt ON w.accountId = mt.id
w.`status` = 1 AND w.feeType = 1
AND DATE_FORMAT(w.createTime,'%Y-%m-%d') >= #{startTime}
AND DATE_FORMAT(w.createTime,'%Y-%m-%d') <= #{endTime}
GROUP BY mt.cagencyId ) fw ON fw.cagencyId = att.childNodeId
t.parentId=0
t.parentId=#{id}
GROUP BY t.id

SELECT *,case reviewStatus when 0 then '不通过' when 1 then '通过' when 2 then '待处理' end reviewStatusStr,
case issuestatus when 0 then '不通过' when 1 then '已发放' when 2 then '待处理' end issuestatusStr
from agy_commission


        and type  =#{type}


        and issuestatus  =#{issuestatus}


        and reviewStatus !=1


        and reviewStatus =#{reviewStatus}


        AND agyAccount = #{agyAccount}


        AND modifyTime  >=  #{startTime}


        AND modifyTime  <=  #{endTime}


order by modifyTime desc

        SELECT commission.*,CASE reviewStatus WHEN 0 THEN '不通过' WHEN 1 THEN '通过' WHEN 2 then '待处理' END reviewStatusStr,
        CASE issuestatus WHEN 0 THEN '不通过' WHEN 1 THEN '已发放' WHEN 2 THEN '待处理' END issuestatusStr, ar.agentType
        FROM agy_commission commission
	    LEFT JOIN (SELECT max(depth) agentType,childnodeid from agy_tree GROUP by childnodeid) ar
       	ON ar.childnodeid = commission.agentid


                AND commission.agyAccount = #{agyAccount} 


                and commission.type  =#{type}


                and commission.issuestatus  =#{issuestatus}


                and commission.reviewStatus =#{reviewStatus}


                AND modifyTime  >=  #{startTime}


                AND modifyTime  <=  #{endTime}


        ORDER BY modifyTime DESC

SELECT * from agy_commission
WHERE issuestatus =1

    AND type = #{type}


    AND agentId = #{agentId}


    AND time =#{time}


    AND agyAccount =#{agyAccount}


    AND modifyTime  >=  #{startTime}


    AND modifyTime  <=  #{endTime}

