mapper.sql:
SELECT username,agyaccount,payout,rate,gamelogoId,b.additionalServicerate,b.cagencyid, CASE WHEN rate > 0 AND payout < 0 THEN TRUNCATE(ABS(rate/100*payout),2)+IFNULL(waterCost,0) ELSE IFNULL(waterCost,0) END cost,depotname FROM( SELECT a.username,ag.agyaccount,SUM(payout) payout, IFNULL(ge.rate,0) rate,lg.id gamelogoId,ag.additionalServicerate, CONCAT(a.depotcode,cat.catname) depotname,SUM(waterCost) waterCost,ag.id cagencyid FROM ( SELECT r.platform, d.id depotId,payout,r.username,r.startday,d.depotcode,r.cost waterCost, CASE WHEN r.gamecategory='Chess' THEN '6' WHEN r.gamecategory='Sport' then '1' WHEN r.gamecategory='Live' then '3' WHEN r.gamecategory='Slot' then '5' WHEN r.gamecategory='Hunter' then '8' WHEN r.gamecategory='Esport' then '9' WHEN r.gamecategory='Lottery' then '12' END type,r.gamecategory FROM rpt_bet_rcd_day r LEFT JOIN t_gm_depot d ON r.platform = d.depotcode WHERE r.gamecategory != '' ) a INNER JOIN mbr_account t ON t.loginname = a.username LEFT JOIN t_game_logo lg ON a.depotid = lg.depotid and lg.catid = a.type LEFT JOIN set_gm_game ge ON ge.gamelogoid = lg.id LEFT JOIN agy_account ag ON ag.id = t.cagencyid LEFT JOIN t_gm_cat cat ON cat.id = a.type WHERE a.type != '' and a.depotId != '' AND ag.feemodel != 2
AND ag.agyAccount =#{agyAccount}
AND a.username =#{loginName}
AND a.startday >= DATE_FORMAT(#{startTime},'%Y-%m-%d')
AND a.startday <= DATE_FORMAT(#{endTime},'%Y-%m-%d')
and t.cagencyid =#{cagencyId}
and t.subcagencyId =#{subcagencyId}
GROUP BY ag.agyaccount,a.depotid,a.type
GROUP BY a.username,a.depotid,a.type
) b

        SELECT t.loginname username,ag.agyaccount,t.id cagencyid,
		        ag.feemodel,ag.depositServicerate,ag.withdrawServicerate,IFNULL(dt.depositAmount,0) depositAmount,
		        IFNULL(aw.withdrawAmount,0) withdrawAmount,
		        TRUNCATE(
				ABS(IFNULL(dt.depositAmount,0) * (ag.depositServicerate/100))
				+ ABS(IFNULL(aw.withdrawAmount,0) * (ag.withdrawServicerate/100))
				,2) serviceCost
        FROM mbr_account t
        LEFT JOIN agy_account ag ON ag.id = t.cagencyid
        LEFT JOIN (
            SELECT IFNULL(SUM(actualarrival),0) depositAmount,t.cagencyid
            FROM fund_deposit d
            LEFT JOIN mbr_account t ON d.accountid = t.id
            WHERE d.`status`=1

                AND d.audittime  >=  #{startTime}


                AND d.audittime  <=  #{endTime}

            GROUP BY t.cagencyid
        ) dt ON dt.cagencyid = ag.id
        LEFT JOIN (
            SELECT IFNULL(sum(actualarrival),0) withdrawAmount,t.cagencyid
            FROM fund_acc_withdraw d
            LEFT JOIN mbr_account t ON d.accountid = t.id
            WHERE d.`status`=1

                AND d.passtime  >=  #{startTime}


                AND d.passtime  <=  #{endTime}

            GROUP BY t.cagencyid
         ) aw ON aw.cagencyid = ag.id
        WHERE ag.feemodel != 1

            AND ag.agyAccount =#{agyAccount}


            AND t.loginname =#{loginName}


            AND t.cagencyid =#{cagencyId}


            AND t.subcagencyId =#{subcagencyId}

        GROUP BY t.cagencyid

WHERE a.type != '' and a.depotId != ''

    AND ag.agyAccount =#{agyAccount}


    AND a.username =#{loginName}


    AND a.startday  >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')


    AND a.startday  <=  DATE_FORMAT(#{endTime},'%Y-%m-%d')


    and t.cagencyid =#{cagencyId}


    and t.subcagencyId =#{subcagencyId}



        GROUP BY ag.agyaccount,a.depotid,a.type


        GROUP BY a.username,a.depotid,a.type

