mapper.sql:
SELECT t.accountId, IFNULL(SUM(t.bonusAmount), 0) totalBonusAmount, t.cagencyId cagencyId, t.subcagencyid subcagencyid from ( -- 红利 SELECT IF(ta.source = 3, -1 * ta.bonusamount, ta.bonusamount) bonusAmount, ta.accountId,m.cagencyId cagencyId,m.subcagencyid subcagencyid from opr_act_bonus ta LEFT JOIN mbr_account m ON ta.accountId = m.id LEFT JOIN agy_account ag ON ag.id = m.cagencyid WHERE ta.status = 1
and DATE_ADD(ta.applicationTime, interval 7 DAY) >= now()
AND ta.applicationTime >= #{startTime}
AND ta.applicationTime <= #{endTime}
union all -- 任务 SELECT t.bonusAmount,t.accountId,m.cagencyId cagencyId,m.subcagencyid subcagencyid from task_bonus t LEFT JOIN mbr_account m ON t.accountId = m.id LEFT JOIN agy_account ag ON ag.id = m.cagencyid WHERE 1=1
and DATE_ADD(t.time, interval 7 DAY) >= now()
AND t.time >= #{startTime}
AND t.time <= #{endTime}
) t GROUP BY t.cagencyId

SELECT (if(sign(SUM(totalPayout))=-1, abs(SUM(totalPayout)), -1*SUM(totalPayout))-SUM(bonusamount)-SUM(taskBonusamount)) totalProfit, loginname,accountid,validBet,cagencyId,subcagencyid from ( SELECT IFNULL(SUM(totalPayout),0) totalPayout,IFNULL(SUM(validBet),0) validBet, loginname,accountid,cagencyid,subcagencyid,SUM(bonusamount) bonusamount, SUM(taskBonusamount) taskBonusamount from ( SELECT IFNULL(SUM(y.payout),0) totalPayout, IFNULL(SUM(validbet),0) validBet,t.loginname,t.id accountid, t.cagencyId,t.subcagencyid,0 bonusamount,0 taskBonusamount from rpt_bet_rcd_day y LEFT JOIN mbr_account t ON y.username = t.loginname LEFT JOIN agy_account t2 ON t2.id = t.cagencyid
AND y.startday >= str_to_date(#{startTime}, '%Y-%m-%d')
AND y.startday <= str_to_date(#{endTime}, '%Y-%m-%d')
GROUP BY y.username
union all
SELECT 0 totalPayout,0 validBet,t.loginname,t.id accountid, t.cagencyId,t.subcagencyid,IFNULL(SUM(s.bonusamount),0) bonusamount, 0 taskBonusamount from opr_act_bonus s LEFT JOIN mbr_account t ON s.accountid = t.id LEFT JOIN agy_account t2 ON t2.id = t.cagencyid WHERE s.status =1
AND s.applicationTime >= #{startTime}
AND s.applicationTime <= #{endTime}
GROUP BY s.accountid
union all
SELECT 0 totalPayout,0 validBet,t.loginname,t.id accountid, t.cagencyId,t.subcagencyid,0 bonusamount, IFNULL(SUM(s.bonusamount),0) taskBonusamount from task_bonus s LEFT JOIN mbr_account t ON s.accountid = t.id LEFT JOIN agy_account t2 ON t2.id = t.cagencyid
AND s.time >= #{startTime}
AND s.time <= #{endTime}
GROUP BY s.accountid
) b GROUP BY accountid ) a GROUP BY accountid

mapper.select:
SELECT a.*, a.payout - a.cost - a.totalBonusAmount - a.serviceCost - a.calculateProfit totalProfit, (a.depositBalance - a.drawAmount) ctDiffer FROM ( SELECT a.*, CASE WHEN (a.feemodel = 2 OR a.feemodel = 3) THEN TRUNCATE(IFNULL(c.serviceCost,0), 2) ELSE 0 END serviceCost, CASE WHEN (a.feemodel = 1 OR a.feemodel = 3) THEN TRUNCATE(IFNULL(SUM(b.cost) + SUM(b.cost) * b.additionalServicerate / 100,0), 2) ELSE 0 END cost FROM ( SELECT * FROM ( SELECT t.agyAccount,IFNULL(ar.subAgentNum,0) subAgentNum, IFNULL(ac.accountNum,0) accountNum,t.createtime registerTime, IFNULL(SUM(tp.validBet),0) validBet, IFNULL(SUM(tp.bet),0) betTotal, IFNULL(SUM(tp.payout),0) payout, IFNULL(f.actualarrival,0) depositBalance,IFNULL(w.actualarrival,0) drawAmount, IFNULL(ca.calculateProfit,0) calculateProfit, IFNULL((SELECT totalBonusAmount from () tb WHERE tb.cagencyId = t.id),0) totalBonusAmount, (SELECT SUM(commission) FROM agy_commission WHERE agentid =t.id AND issuestatus =1 AND time=#{time}) commission, t.id, IFNULL(um.activeAccountNum,0) activeAccountNum, IFNULL(act.commissioncap,0) commissioncap, IFNULL(newde.totalNewDepositAmount, 0) totalNewDepositAmount, IFNULL(newde.totalNewDeposits, 0) totalNewDeposits, t.feemodel feeModel, IFNULL(audit.auditAmAmount,0) auditAmAmount FROM agy_account t LEFT JOIN () tp ON tp.cagencyId = t.id
LEFT JOIN ( SELECT d.accountid,SUM(d.actualarrival) actualarrival ,t.cagencyId FROM fund_deposit d LEFT JOIN mbr_account t on d.accountid = t.id WHERE d.status=1
AND d.audittime >= #{startTime}
AND d.audittime <= #{endTime}
GROUP BY t.cagencyId ) f ON f.cagencyId = t.id
LEFT JOIN ( SELECT d.accountid,SUM(d.actualarrival) actualarrival,t.cagencyId from fund_acc_withdraw d LEFT JOIN mbr_account t on d.accountid = t.id WHERE d.status=1
AND d.passtime >= #{startTime}
AND d.passtime <= #{endTime}
GROUP BY t.cagencyId ) w ON w.cagencyId = t.id
LEFT JOIN ( SELECT t.id accountid,SUM(IF(audit.financialCode = 'AM', -1 * audit.amount, audit.amount)) calculateProfit, t.cagencyId FROM fund_audit audit LEFT JOIN mbr_account t ON audit.accountid = t.id WHERE audit.status = 1 AND audit.isCalculateProfit = 1
AND audit.createTime >= #{startTime}
AND audit.createTime <= #{endTime}
GROUP BY t.cagencyId ) ca ON ca.cagencyId = t.id
LEFT JOIN ( SELECT t.id accountid,IFNULL(SUM(audit.amount),0) auditAmAmount, t.cagencyId FROM fund_audit audit LEFT JOIN mbr_account t ON audit.accountid = t.id WHERE audit.status = 1 AND audit.financialcode = 'AM'
AND audit.audittime >= #{startTime}
AND audit.audittime <= #{endTime}
GROUP BY t.cagencyId ) audit ON audit.cagencyId = t.id
LEFT JOIN ( SELECT COUNT(*) subAgentNum,parentid from agy_tree WHERE depth=1 GROUP BY parentid ) ar ON ar.parentid = t.id LEFT JOIN ( SELECT COUNT(*) accountNum,cagencyId from mbr_account GROUP BY cagencyId ) ac ON ac.cagencyId = t.id LEFT JOIN ( SELECT t.cagencyId, COUNT(DISTINCT t.id) activeAccountNum FROM ( SELECT account.id,cagencyId,SUM(ry.bet) bet FROM rpt_bet_rcd_day ry INNER JOIN mbr_account account ON ry.username=account.loginName
AND ry.startday >= str_to_date(#{startTime}, '%Y-%m-%d')
and ry.startday <= str_to_date(#{endTime}, '%Y-%m-%d')
GROUP BY account.id HAVING bet >= 100 ) t GROUP BY cagencyId ) um ON um.cagencyId = t.id LEFT JOIN agy_contract act ON act.id= t.contractid LEFT JOIN ( SELECT COUNT(DISTINCT d.id) totalNewDeposits, SUM(d.actualarrival) totalNewDepositAmount, agy.agyaccount FROM fund_deposit t INNER JOIN( SELECT accountid, MIN(id) id, actualarrival FROM fund_deposit WHERE status = 1 AND actualarrival > 0 GROUP BY accountid ) d ON t.id = d.id LEFT JOIN mbr_account mbr ON mbr.id = d.accountId LEFT JOIN agy_account agy ON agy.id = mbr.cagencyid
AND t.auditTime >= #{startTime}
AND t.auditTime < #{endTime}
GROUP BY agy.agyaccount ) newde ON newde.agyaccount = t.agyaccount
AND EXISTS( SELECT e1.childNodeId FROM agy_tree e1 INNER JOIN agy_account t1 ON e1.parentId = t1.id WHERE t1.id =#{subAgentId} AND e1.depth=1 AND t.id = e1.childNodeId )
AND t.agyaccount =#{agyAccount}
GROUP BY t.id ) a
ORDER BY ${orderBy}
ORDER BY registerTime DESC
)a LEFT JOIN () b ON a.agyAccount = b.agyaccount LEFT JOIN () c ON a.agyAccount = c.agyaccount GROUP BY a.agyAccount
ORDER BY ${orderBy}
)a
ORDER BY ${orderBy}

SELECT * FROM ( SELECT a.agyaccount,a.createtime registerTime,IFNULL(ac.accountNum,0) accountNum, IFNULL(SUM(tp.validBet),0) validBet, IFNULL(SUM(tp.bet),0) betTotal, IFNULL(SUM(tp.payout),0) payout,a.id, IFNULL(um.activeAccountNum,0) activeAccountNum, IFNULL(newde.totalNewDeposits, 0) totalNewDeposits, IFNULL(f.actualarrival,0) depositBalance,IFNULL(w.actualarrival,0) drawAmount, (IFNULL(f.actualarrival,0)-IFNULL(w.actualarrival,0)) ctDiffer, IFNULL((SELECT totalBonusAmount FROM () tb WHERE tb.subcagencyid = a.id),0) totalBonusAmount, IFNULL(newde.totalNewDepositAmount, 0) totalNewDepositAmount, IFNULL(SUM(audit.auditAmAmount),0) auditAmAmount, IFNULL(SUM(ca.calculateProfit),0) calculateProfit FROM agy_account a LEFT JOIN () tp ON tp.subcagencyid = a.id LEFT JOIN ( SELECT d.accountid,SUM(d.actualarrival) actualarrival ,t.subcagencyid from fund_deposit d LEFT JOIN mbr_account t on d.accountid = t.id WHERE d.status=1
AND d.audittime >= #{startTime}
AND d.audittime <= #{endTime}
GROUP BY t.subcagencyid ) f ON f.subcagencyid = a.id LEFT JOIN ( SELECT d.accountid,SUM(d.actualarrival) actualarrival,t.subcagencyid from fund_acc_withdraw d LEFT JOIN mbr_account t on d.accountid = t.id WHERE d.status=1
AND d.passtime >= #{startTime}
AND d.passtime <= #{endTime}
GROUP BY t.subcagencyid ) w ON w.subcagencyid = a.id
LEFT JOIN ( SELECT COUNT(*) accountNum,subcagencyid from mbr_account GROUP BY subcagencyid ) ac ON ac.subcagencyid = a.id LEFT JOIN ( SELECT COUNT(DISTINCT account.id) activeAccountNum,subcagencyid FROM rpt_bet_rcd_day ry INNER JOIN mbr_account account ON ry.username=account.loginName WHERE account.subcagencyid is not null and ry.bet >= 100
AND ry.startday >= str_to_date(#{startTime}, '%Y-%m-%d')
and ry.startday <= str_to_date(#{endTime}, '%Y-%m-%d')
GROUP BY account.subcagencyid ) um ON um.subcagencyid = a.id
LEFT JOIN ( SELECT t.id accountid,SUM(IF(audit.financialCode = 'AM', -1 * audit.amount, audit.amount)) calculateProfit, t.subcagencyid FROM fund_audit audit LEFT JOIN mbr_account t ON audit.accountid = t.id WHERE audit.status = 1 AND audit.isCalculateProfit = 1
AND audit.audittime >= #{startTime}
AND audit.audittime <= #{endTime}
GROUP BY t.subcagencyid ) ca ON ca.subcagencyid = a.id
LEFT JOIN ( SELECT t.id accountid,IFNULL(SUM(audit.amount),0) auditAmAmount, t.subcagencyid FROM fund_audit audit LEFT JOIN mbr_account t ON audit.accountid = t.id WHERE audit.status = 1 AND audit.financialcode = 'AM'
AND audit.audittime >= #{startTime}
AND audit.audittime <= #{endTime}
GROUP BY t.subcagencyid ) audit ON audit.subcagencyid = a.id
LEFT JOIN ( SELECT COUNT(DISTINCT d.id) totalNewDeposits, SUM(d.actualarrival) totalNewDepositAmount, agy.agyaccount FROM fund_deposit t INNER JOIN( SELECT accountid, MIN(id) id, actualarrival FROM fund_deposit WHERE status = 1 AND actualarrival > 0 GROUP BY accountid ) d ON t.id = d.id LEFT JOIN mbr_account mbr ON mbr.id = d.accountId LEFT JOIN agy_account agy ON agy.id = mbr.subcagencyid
AND t.createtime >= #{startTime}
AND t.createtime < #{endTime}
GROUP BY agy.agyaccount ) newde ON newde.agyaccount = a.agyaccount WHERE a.parentid = #{cagencyId} and a.attributes=1
AND a.agyaccount =#{agyAccount}
GROUP BY a.agyaccount ) a
ORDER BY ${orderBy}
ORDER BY registertime desc
