mapper.select:
select
tt.promotionNum
tt.${columnName}
,tt.isOnline,tt.isVerifyMoblie,tt.domaincode,tt.departmentid, tt.offlinetime, tt.betPoint
from ( SELECT DISTINCT t1.id,t1.loginName,t1.realName,t1.cagencyId,t1.tagencyId,t1.groupId,t1.registerTime,t1.loginTime,t1.available,t1.actLevelId,t1.isActivityLock, t1.isOnline,t1.isVerifyMoblie,t1.domaincode,t1.agyflag,t1.agyLevelId,t1.offlinetime, wa.balance totalBalance, mal.tierName, t0.agyAccount,t0.id agyAccountId,t0.departmentid, t4.registerIp,t4.registerUrl,t4.registerSource, t5.groupName, -- (SELECT createTime FROM fund_deposit WHERE accountId=t1.id AND`status`=1 LIMIT 1 ) AS depositTime, -- (select loginarea from log_mbrlogin where accountId=t1.id order by logintime desc limit 1) AS loginArea, null loginArea, null loginType, -- (select logintype from log_mbrlogin where accountId=t1.id order by logintime desc limit 1) AS loginType, IFNULL(a.promotionNum,0) promotionNum, pma.loginname parentLoginName, fe.createTime depositTime, t1.labelid, mao.betPoint FROM mbr_account t1 LEFT JOIN agy_account t0 on t1.cagencyId = t0.id LEFT JOIN mbr_wallet wa on t1.loginName = wa.loginName LEFT JOIN log_mbrregister t4 on t1.loginName=t4.loginName LEFT JOIN mbr_group t5 on t1.groupId=t5.id LEFT JOIN mbr_activity_level mal on t1.actLevelId = mal.id LEFT JOIN (select count(1) promotionNum,tr.parentId from mbr_tree tr where tr.depth = 1 GROUP BY tr.parentId) a ON a.parentId = t1.id LEFT JOIN mbr_tree mt on mt.childnodeid = t1.id and mt.depth = 1 LEFT JOIN mbr_account pma on mt.parentid = pma.id LEFT JOIN mbr_account_mobile mb on mb.accountid = t1.id LEFT JOIN mbr_bankcard_history mbh on mbh.accountid = t1.id LEFT JOIN (SELECT MIN(id),accountId, createTime from fund_deposit where status = 1 GROUP BY accountid) fe ON fe.accountId = t1.id LEFT JOIN mbr_account_other mao ON mao.accountid = t1.id
)tt
AND tt.promotionNum>=#{minPromotionNum}
AND tt.promotionNum<=#{maxPromotionNum}
AND tt.loginType=#{loginType}

SELECT t.username loginName, MAX(t.startday) betDate FROM ( SELECT username, SUM(validbet) validbetTotal, startday FROM rpt_bet_rcd_day WHERE username IN
#{item}
GROUP BY username, startday HAVING validbetTotal >= 100 ) t GROUP BY t.username

select
tt.${columnName}
from ( select t.id, t.available, t.groupId, t.actLevelId, t.isActivityLock, mal.tiername, t.tagencyId, t.cagencyId, w.balance as totalBalance, t2.loginName referrer from mbr_account t LEFT JOIN mbr_wallet w ON t.id = w.accountId LEFT JOIN mbr_tree tree ON t.id = tree.childNodeId AND tree.childNodeId = t.id AND tree.depth =1 LEFT JOIN mbr_account t2 ON tree.parentId = t2.id LEFT JOIN mbr_activity_level mal on t.actLevelId = mal.id where t.id = #{id} )tt

