SELECT * FROM (
SELECT r.registerIp item,r.loginName,y.agyaccount FROM log_mbrregister r LEFT JOIN mbr_account t ON t.id = r.accountid LEFT JOIN agy_account y ON y.id = t.cagencyid WHERE r.registerIp=#{item} AND r.accountId !=#{accountId}
SELECT DISTINCT l.loginName,l.loginIp,l.logintime item,y.agyaccount FROM log_mbrlogin n INNER JOIN log_mbrlogin l ON n.loginIp = l.loginIp LEFT JOIN mbr_account t ON t.id = l.accountid LEFT JOIN agy_account y ON y.id = t.cagencyid WHERE n.accountId = #{accountId} AND l.accountId != #{accountId}
SELECT t.loginName,n.agyAccount item FROM mbr_account t LEFT JOIN agy_account n ON t.cagencyId = n.id WHERE t.id !=#{accountId} AND t.cagencyId =#{item}
SELECT email item,loginName FROM mbr_account WHERE email=#{item} AND id !=#{accountId} AND email is not null AND email !=''
SELECT qq item,loginName FROM mbr_account WHERE qq=#{item} AND id !=#{accountId} AND qq is not null AND qq !=''
SELECT l.loginName,l.deviceUuid item,l.createtime,y.agyaccount FROM mbr_account_device n INNER JOIN mbr_account_device l ON n.deviceuuid = l.deviceuuid LEFT JOIN mbr_account t ON t.id = l.accountid LEFT JOIN agy_account y ON y.id = t.cagencyid WHERE n.accountId = #{accountId} AND l.accountId != #{accountId} and n.deviceuuid != '' order by l.createtime desc
SELECT realname item,loginName FROM mbr_account WHERE realname=#{item} AND id !=#{accountId} AND realname is not null AND realname !=''
SELECT la.name item, t.loginName, y.agyaccount FROM mbr_account t LEFT JOIN agy_account y ON y.id = t.cagencyid LEFT JOIN mbr_label la ON la.id = t.labelid WHERE t.labelid = #{item} AND t.id !=#{accountId}
) t

