mapper.select:
SELECT count(tb1.username) total,sum(tb1.bet) betTotal,sum(tb1.validbet) validbetTotal,sum(tb1.payout) payoutTotal FROM ( SELECT rbrd.username,sum(rbrd.bet) bet,sum(rbrd.validbet) validbet,sum(rbrd.payout) payout,ma.parentId FROM rpt_bet_rcd_day rbrd INNER JOIN ( SELECT act.loginName,e.childNodeId,e.parentId FROM mbr_account ma INNER JOIN agy_account t ON t.id = ma.cagencyId INNER JOIN mbr_tree e ON e.parentId = ma.id INNER JOIN mbr_account act ON act.id = e.childNodeId WHERE ma.loginName = #{loginName}
AND e.depth >0
) ma ON rbrd.username= ma.loginName
GROUP BY rbrd.username ) tb1

-- 4.汇总数据 select a.level,a.pagy username, count(a.username) total,sum(a.bet) betTotal,sum(a.validbet) validbetTotal,sum(a.payout) payoutTotal from( -- 3.按会员group, 便于统计个数 SELECT a.pid,a.pagy,rbrd.username,a.level, sum(rbrd.bet) bet,sum(rbrd.validbet) validbet,sum(rbrd.payout) payout FROM rpt_bet_rcd_day rbrd INNER JOIN mbr_account ma ON rbrd.username= ma.loginName INNER JOIN ( -- 2.得到目标代理下所有的子代(不含目标代理) select t.pid,t.pagy, e.childnodeid, a.agyaccount, t.level from agy_tree e inner join ( -- 1.找到目标代理 （股东-找所有的总代，代理-找到下级代理)
select t.id pid, t.agyaccount pagy, t.parentid level from agy_account t where t.parentid = 0
select t1.id pid, t1.agyaccount pagy, t1.parentid level from agy_account t inner join agy_tree e on t.id = e.parentid and depth = 1 inner join agy_account t1 on e.childnodeid = t1.id where t.agyAccount = #{agyAccount}
)t on e.parentid = t.pid inner join agy_account a on a.id = e.childnodeid where e.depth >0 )a ON a.childnodeid = ma.cagencyId
group by rbrd.username )a group by a.pid
